'  P_HOST_NAME VARCHAR2(200);',
'  P_TICKET_REF VARCHAR2(200);',
'  P_ENV_SOURCE VARCHAR2(200);',
'  P_DC_LOCATION VARCHAR2(200);',
'  P_PATCH_TYPE VARCHAR2(200);',
'  P_PATCH_FILE VARCHAR2(200);',
'  P_ORACLE_DB_HOME VARCHAR2(200);',
'  P_JOB_ID NUMBER;',
'  P_REQUEST_ID NUMBER;',
'    ',
'BEGIN',
'',
' for c1 in (',
'SELECT',
'    id,',
'    template_type,',
'    template_name,',
'    job_name,',
'    request_type,',
'    host_name,',
'    cluster_name,',
'    ticket_ref,',
'    dc_location,',
'    rdbms_home,',
'    patch_file_name,',
'    status,',
'    created,',
'    created_by,',
'    updated,',
'    updated_by,',
'    job_id,',
'    request_id',
'FROM',
'    v_patch_request_queue q',
'where',
'  q.id = :P410_ACTION_ID',
'',
') loop',
'  P_HOST_NAME := c1.host_name;',
'  P_TICKET_REF := c1.ticket_ref;',
'',
'  P_ENV_SOURCE := NULL;',
'  P_DC_LOCATION := c1.dc_location;',
'--ORACLE or GRID',
'  P_PATCH_FILE := c1.patch_file_name;',
' P_ORACLE_DB_HOME  := c1.rdbms_home;',
'  P_JOB_ID := NULL;',
'  P_REQUEST_ID := NULL;',
' -- What type of playbook',
'if c1.template_name like ''psu-patching-grid'' then',
'',
'P_PATCH_TYPE := ''GRID'';',
'  OAC$ANSIBLE_REST_UTL.do_grid_psu_apply_rest(  P_HOST_NAME => P_HOST_NAME,',
'P_TICKET_REF => P_TICKET_REF,',
'P_ENV_SOURCE => P_ENV_SOURCE,',
'P_DC_LOCATION => P_DC_LOCATION,',
'P_PATCH_TYPE => P_PATCH_TYPE,',
'P_PATCH_FILE => P_PATCH_FILE,',
'P_ORACLE_DB_HOME => P_ORACLE_DB_HOME,',
'P_JOB_ID => P_JOB_ID,',
'P_REQUEST_ID => P_REQUEST_ID) ;  ',
'',
'elsif c1.template_name like ''psu-patching-rdbms'' then',
' null;',
'  P_PATCH_TYPE := ''ORACLE''; ',
