'P_JOB_ID => P_JOB_ID,',
'P_REQUEST_ID => P_REQUEST_ID) ;  ',
'',
'end if;',
'UPDATE  v_patch_request_queue',
' set JOB_ID = P_JOB_ID',
' ,REQUEST_ID  = P_REQUEST_ID ',
' ,status = ''E''',
'where',
'id =  c1.ID;',
'',
'end loop;',
'',
'END;',
'',
''))
