,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'DECLARE',
'  l_arr OWA.vc_arr;',
'  l_priv OWA.vc_arr;',
'  l_modules OWA.vc_arr;',
'  l_patterns OWA.vc_arr;',
'  l_modules_v varchar2(32000);',
'  l_modules_all_v varchar2(32000);',
'  i number := 1;',
'BEGIN',
'',
'',
'--',
'-- We use ORDS.DEFINE_PRIVILEGE to do all the adds  and deletes',
'--  essentially populating the l_modules array with the list of modules we want to protect',
'--',
'  l_arr(1) := :P400_ROLE;',
'  l_priv(1) := :P400_GRANT_PRIV;',
'',
'',
'if :REQUEST = ''ADDMODULE'' then',
'   ',
'  l_priv(1) := :P400_GRANT_PRIV;',
' --',
' -- Get  list of all the current modules for the select priv',
' ',
' for c1 in (',
' select module_name ',
' from  user_ords_privilege_modules where privilege_name = :P400_GRANT_PRIV',
'   ) loop',
'    l_modules(i) := c1.module_name;',
'    i := i +1;',
'   end loop;',
'-- Then add the current selected module to it',
'--   i := i +1;',
'l_modules(i) := :P400_GRANT_MODULE;',
'',
'',
' ORDS.DEFINE_PRIVILEGE(',
'   p_privilege_name   => l_priv(1),',
'   p_roles            => l_arr,',
'   p_patterns    => l_patterns,',
'   p_modules          => l_modules',
' );',
'',
'/*Single process switch to define for both add and remove',
'ORDS.set_module_privilege(',
'    p_privilege_name => :P400_GRANT_PRIV,',
'    p_module_name => :P400_GRANT_MODULE',
'  );    ',
'*/',
'',
'elsif  :REQUEST = ''DELETEMODULE'' then',
' ',
'-- Get  list of all the current modules for the select priv',
' ',
' for c1 in (',
' select module_name ',
' from  user_ords_privilege_modules ',
'     where privilege_name = :P400_GRANT_PRIV',
'     and module_name != :P400_GRANT_MODULE',
'   ) loop',
'    l_modules(i) := c1.module_name;',
'    i := i +1;',
'   end loop;',
'-- Then add the current selected module to it',
'',
' ORDS.DEFINE_PRIVILEGE(',
'   p_privilege_name   => l_priv(1),',
'   p_roles            =>  l_arr,',
'   p_patterns    => l_patterns,',
'   p_modules          => l_modules',
' );',
'null;',
'/*  Single process doesnt work',
'ORDS.set_module_privilege(',
'    p_module_name => :P400_GRANT_MODULE',
'  );    ',
'*/',
'',
'elsif  :REQUEST = ''PROTECTALL'' then',
' ',
'-- Get  list of all the current modules for the select priv',
' ',
' for c1 in (',
' select name  module_name',
' from  user_ords_modules      ',
'   ) loop',
'    l_modules(i) := c1.module_name;',
'    i := i +1;',
'   end loop;',
'-- Then add the current selected module to it',
'',
' ORDS.DEFINE_PRIVILEGE(',
'   p_privilege_name   => l_priv(1),',
'   p_roles            =>  l_arr,',
'   p_patterns    => l_patterns,',
'   p_modules          => l_modules',
' );',
'null;',
'/*  Single process doesnt work',
'ORDS.set_module_privilege(',
'    p_module_name => :P400_GRANT_MODULE',
'  );    ',
'*/',
'',
'elsif  :REQUEST = ''REMOVEALL'' then',
' ',
'-- Get  list of all the current modules for the select priv',
' ',
' ORDS.DEFINE_PRIVILEGE(',
'   p_privilege_name   => l_priv(1),',
'   p_roles            =>  l_arr,',
'   p_patterns    => l_patterns,',
'   p_modules          => l_modules',
' );',
'null;',
'/*  Single process doesnt work',
'ORDS.set_module_privilege(',
'    p_module_name => :P400_GRANT_MODULE',
'  );    ',
'*/',
'',
'',
'',
'/*',
'ORDS.DEFINE_PRIVILEGE(',
'   p_privilege_name    IN sec_privileges.name%type,',
'   p_roles             IN owa.vc_arr,',
'   p_patterns          IN owa.vc_arr,',
'   p_modules           IN owa.vc_arr,',
'   p_label             IN sec_privileges.label%type DEFAULT NULL,',
'   p_description       IN sec_privileges.description%type DEFAULT NULL,',
'   p_comments          IN sec_privileges.comments%type DEFAULT NULL);',
'',
'select listagg(name,'','') within group (order by name)  ords_modules_all into l_modules_all_v  from user_ords_modules',
'group by  schema_id;',
'',
'*/',
'',
'',
'',
'end if;',
'',
'  ',
'  COMMIT;',
'',
'END;',
''))
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
,p_process_when=>'ADDMODULE:DELETEMODULE:PROTECTALL:REMOVEALL'
,p_process_when_type=>'REQUEST_IN_CONDITION'
,p_process_success_message=>'Action Processed'
);
end;
