,p_process_name=>'RUN_ACL'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'l_sql clob := ''select ',
'   sys_context(~USERENV~,~SESSION_USER~) ',
'from dual'';',
'',
'l_apex_clob varchar2(4000) := ''',
' declare',
'    l_acl_path varchar2(4000);',
'    l_apex_schema varchar2(100);',
'    l_filename varchar2(30) := ~&FILENAME&.com.xml~;',
'    l_schema varchar2(20) := ~&SCHEMA&~;',
'',
'begin',
'    for c1 in (select schema',
'                 from sys.dba_registry',
'                where comp_id = ~APEX~) loop',
'        l_apex_schema := c1.schema;',
'    end loop;',
'',
'    sys.dbms_network_acl_admin.append_host_ace(',
'        host => ~*~,',
'        ace => xs$ace_type(privilege_list => xs$name_list(~connect~),',
'        principal_name => l_apex_schema,',
'        principal_type => xs_acl.ptype_db));',
'    commit;',
'',
'    BEGIN',
'    DBMS_NETWORK_ACL_ADMIN.CREATE_ACL(',
'          acl           => l_filename',
'        , description   => ~All requests to test utl_http over &DOMAIN&~',
'        , principal     => l_schema -- schema name',
'        , is_grant      => TRUE',
'        , privilege     => ~connect~',
'    );',
'',
'  EXCEPTION WHEN OTHERS THEN',
'        NULL; -- ACL already exist yet',
'    DBMS_NETWORK_ACL_ADMIN.ADD_PRIVILEGE(',
'          acl       => l_filename',
'        , principal => l_schema -- schema name',
'        , is_grant  => TRUE',
'        , privilege => ~connect~',
'    );',
'',
'END;',
'',
' DBMS_NETWORK_ACL_ADMIN.ADD_PRIVILEGE(',
'          acl       => l_filename',
'        , principal => l_schema -- schema name',
'        , is_grant  => TRUE',
'        , privilege => ~resolve~',
'    );',
'',
'    DBMS_NETWORK_ACL_ADMIN.ASSIGN_ACL(',
'          acl           => l_filename',
'        , host          => ~*.&DOMAIN&~',
'        , lower_port    => 443',
'    );',
'',
'    DBMS_NETWORK_ACL_ADMIN.ASSIGN_ACL(',
'          acl           => l_filename',
'        , host          => ~*.&DOMAIN&~',
'        , lower_port    => 443',
'    );',
'',
'    DBMS_NETWORK_ACL_ADMIN.ASSIGN_ACL(',
'        acl             => l_filename',
'      , host            => ~*.&DOMAIN&~',
'      , lower_port      => 80',
'  );',
'',
' commit; ',
'END;',
''';',
'',
'l_result varchar2(1000);',
'l_sqlerrm varchar2(4000);',
'Begin ',
'null;',
'l_sql := replace(l_sql,''~'','''''''');',
'',
'l_apex_clob := replace(replace(replace(replace(l_apex_clob,''~'','''''''')',
'                           ,''&FILENAME&'',:P501_FILENAME_ACL) ',
'                           ,''&SCHEMA&'',:P501_SCHEMA)',
'                           ,''&DOMAIN&'',:P501_DOMAIN); ',
'',
'execute immediate l_sql into l_result;',
'execute immediate replace(l_apex_clob,''/'');',
':P501_RESULT :=  ''ACL Run By ''||l_result||'' Sucessfully.'';',
'',
'exception ',
' when others then',
'  :P501_RESULT := ''<pre>''||sqlerrm||''</pre>'';',
'',
'',
'end;'))
